import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import './Card.css'

const useStyles = makeStyles({
  card: {
    minWidth: 275,
    maxWidth: 400
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  a: {
    textDecoration: 'none'
  }
});

export default function SimpleCard(props) {
  const myLink = `https://www.coingecko.com/en/coins/${props.data.coin.id}/usd` //template literal
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;
  console.log('TESTING FROM CARD 1', props.data)
  return (
    // console.log('TESTING FROM CARD 1', props.aboutCoins)
    <Card className={classes.card}>
      <CardContent>
        <Typography className={classes.title} color="textSecondary" gutterBottom>
          Word of the Day
        </Typography>
        <Typography variant="h5" component="h2">
          be
          {bull}
          nev
          {bull}o{bull}
          lent
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          adjective
        </Typography>
        <Typography variant="body2" component="p">
          well meaning and kindly.
          <br />
          {'"a benevolent smile"'}
        </Typography>
      </CardContent>
      <CardActions>
      <Button size="small"><a href={myLink} className={classes.a}>Learn More</a></Button>
      </CardActions>
    </Card>
   ) }
