import {FETCH_COINS} from './types';
import Axios from 'axios';

//GET COINS IN THE ACTION, WE SEND THEM TO REDUCER
export const fetchCoins = () => dispatch => { //do dispatch here, like a return statement
    console.log('fetching')
    Axios.get('https://api.coingecko.com/api/v3/coins?order=rank_desc&per_page=5')
    .then (coins => 
        dispatch({
            type: FETCH_COINS,
            payload: coins
        }))
}
