import React, {Component} from 'react';
import { connect } from 'react-redux';
import { fetchCoins } from '../actions/coinActions';
import SimpleTable from './Table'; //Where our coins data is being mapped/rendered/called/WHATEVER!

class Coins extends Component {
    //Wait for coins data before mounting
   componentDidMount() {
       console.log('before did mount');
       this.props.fetchCoins();
   }
   handleChange = (e) => {
    console.log(e.target.value)
    //console.log(state.coins.items)
    //console.log('test', this.props.coinsProps.data)
    this.setState({
      search: e.target.value
    })
    console.log('state after', this.state.search)
    this.handleSearch()
  }
   render() {
       let coinItems = '';
       console.log(this.props.coinsProps.data);//coinsProps is our state turned into props
       if (this.props.coinsProps.data) { //if there is data...
                coinItems = <SimpleTable //return this component with data fed into it
                   data={this.props.coinsProps.data}
                    />
               
       } else {
           coinItems = 'Loading...'; 
       }
       console.log(this.props); //else return coinItems = loading...
       return ( 
           <div>
               <h2>Search</h2>
               <input type='text' value='search' />

               {coinItems} 
           </div>
       );
   }
}
//This turns our state into props we can use throughout the app
const mapStateToProps = state => ({
   coinsProps: state.coins.items
})

//This connects react to redux
export default connect(mapStateToProps, { fetchCoins } )(Coins);
