import React, {Component} from 'react';
import { connect } from 'react-redux';
import { fetchCoins } from '../actions/coinActions';
import SimpleTable from './Table'; //Where our coins data is being mapped/rendered/called/WHATEVER!

class Coins extends Component {
   componentDidMount() {
       console.log('before did mount');
       this.props.fetchCoins();
   }
   render() {
       let coinItems = '';
       console.log(this.props.coinsProps.data);
       if (this.props.coinsProps.data) {
           // coinItems = this.props.coins.data.map( coin => (
               return (<SimpleTable
                   data={this.props.coinsProps.data}
               />
               )
           // ));
       } else {
           coinItems = 'Loading...';
       }
       console.log(this.props);
       return (
           <div>
               {coinItems}
           </div>
       );
   }
}
const mapStateToProps = state => ({
   coinsProps: state.coins.items
})


export default connect(mapStateToProps, { fetchCoins } )(Coins);
