import {ADD_FAVOURITE, REMOVE_FAVOURITE} from './types';


// => dispatch =>

//FAVOURITE ACTIONS
export const addFav = (coin) =>  {
	return {
		type: ADD_FAVOURITE,
		payload: coin,
	};
}


export const remFav = (coin) => {
	return {
		type: 'REMOVE_FAVOURITE',
		payload: coin,
	}
}
