import React, {Component} from 'react';
import './App.css';
import {Provider} from 'react-redux';
import store from './store';
import Coins from './components/Coins';
import NavBar from './components/NavBar';

//ROUTES
import { BrowserRouter, Route } from 'react-router-dom';

//IMPORT PAGES
import About from './pages/About';
import CoinInfo from './pages/CoinInfo';
import Favourites from './pages/Favourites';



//WRAP EVERYTHING IN PROVIDER - ADD STORE
class App extends Component {

//STATE
state = {
  search: ''
}

//console.log('state', state.search)




  handleChange = (e) => {
    console.log(e.target.value)
    //console.log(state.coins.items)
    //console.log('test', this.props.coinsProps.data)
    this.setState({
      search: e.target.value
    })
    console.log('state after', this.state.search)
    this.handleSearch()
  }

  handleSearch = () => {
    let filteredCoins = []
    if(this.state.search !== '') {
      filteredCoins = Coins.items.name.filter.indexOf(this.state.search) !== -1
      console.log('filtered',filteredCoins)
      return filteredCoins
    }
    console.log('filtered', filteredCoins)
  }

  render() {
    return (
      <Provider store={store}> 
      <BrowserRouter>
          <div className="App">
            <NavBar />
             <h3> Top 100 Bitcoins from Coin Gecko!</h3>
              <input type="text" placeholder="Search..." name="Search" onChange={this.handleChange} />
                <Route exact path='/' render={() => <Coins />} />
                <Route path = '/Home' render = {() =>  <Coins />} />
                <Route path='/About' component={About} />
                <Route path = '/Favourites' component={Favourites} />
                <Route path='/CoinInfo' component={CoinInfo} />	
          </div>
        </BrowserRouter>
      </Provider>
    );
  }  
}

export default App;
